string input = "";

var markerIndex = FindMarker(input);

static int FindMarker(string input)
{
    Queue<char> markerQueue = new Queue<char>();
    var queueMaximum = 14;

    for(int i = 0; i< input.Length; i++)
    {
        if (markerQueue.Count == queueMaximum)
            markerQueue.Dequeue();

        markerQueue.Enqueue(input.ElementAt(i));
        
        if(markerQueue.Count == queueMaximum)
        {
            if (markerQueue.Distinct().Count() == queueMaximum)
                return i+1;
        }
    }

    return -1;
}
